                  setRows([...updatedRows]);
                }} value={row.columns.toString()}>
                        <ButtonGroupButton label="1" value="1" name={`options-${row.index}`} />
                        <ButtonGroupButton label="2" value="2" name={`options-${row.index}`} />
                        <ButtonGroupButton label="3" value="3" name={`options-${row.index}`} />
                        <ButtonGroupButton label="4" value="4" name={`options-${row.index}`} />
                        <ButtonGroupButton label="5" value="5" name={`options-${row.index}`} />
                        <ButtonGroupButton label="6" value="6" name={`options-${row.index}`} />
