({
  prefab,
  save,
  close,
  components: {
    ModelSelector,
    Header,
    Content,
    Field,
    Footer,
    PropertiesSelector,
    Text
  },
  helpers: {
    useModelQuery
  }
}) => {
  const [modelId, setModelId] = React.useState('');
  const [properties, setProperties] = React.useState([]);
  const [showValidation, setShowValidation] = React.useState(false);
  const {
    data
  } = useModelQuery({
    variables: {
      id: modelId
    },
    skip: !modelId
  });
  React.useEffect(() => {
    setProperties([]);
  }, [data]);
  return <>
        <Header onClose={close} title="Configure form fields" />
        <Content>
          <Field label="Select model" error={showValidation && <Text color="#e82600">Model is required</Text>}>
            <ModelSelector onChange={id => {
          setShowValidation(false);
          setModelId(id);
        }} value={modelId} />
          </Field>

          <Field label="Select properties">
            <PropertiesSelector onChange={value => {
          setProperties(value);
        }} value={properties} modelId={modelId} scopedModels={false} disabledNames={['created_at', 'updated_at', 'id']} disabledKinds={['BELONGS_TO', 'HAS_AND_BELONGS_TO_MANY', 'HAS_MANY', 'MULTI_FILE', 'AUTO_INCREMENT', 'COUNT', 'EMAIL', 'MULTI_IMAGE', 'PDF', 'RICH_TEXT', 'SIGNED_PDF', 'SUM', 'BOOLEAN_EXPRESSION', 'DATE_EXPRESSION', 'DATE_TIME_EXPRESSION', 'DECIMAL_EXPRESSION', 'INTEGER_EXPRESSION', 'MINUTES_EXPRESSION', 'PRICE_EXPRESSION', 'STRING_EXPRESSION', 'TEXT_EXPRESSION']} />
          </Field>
        </Content>
        <Footer onClose={close} onSave={() => {
      const camelToSnakeCase = str => str[0].toLowerCase() + str.slice(1, str.length).replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);

      if (!modelId || !data || !data.model) {
        setShowValidation(true);
        return;
      }

      const newPrefab = { ...prefab
      };

      if (data && data.model) {
        newPrefab.variables[1].name = camelToSnakeCase(data.model.label);
      }

      newPrefab.structure[0].options[0].value.modelId = modelId;
      newPrefab.structure[0].options[1].value = modelId;
      newPrefab.variables[0].options.modelId = modelId;
      newPrefab.variables[1].options.modelId = modelId;
      newPrefab.actions[0].events[0].options.modelId = modelId;
      newPrefab.actions[0].events[0].options.assign = properties.map(property => ({
        leftHandSide: property.id[0],
        ref: {
          path: ['#customModelVariableId', `#attribute_${property.id[0]}`]
        }
      }));
      const descendantsArray = properties.map(property => {
        switch (property.kind) {
          case 'INTEGER':
            {
              return {
                name: 'TextField',
                options: [{
                  value: {
                    label: [property.label],
                    value: [{
                      id: property.id,
                      type: property.type
                    }],
                    propertyIds: property.id,
                    ref: {
                      id: `#attribute_${property.id[0]}`
                    }
                  },
                  label: 'Label',
                  key: 'customModelAttribute',
                  type: 'CUSTOM_MODEL_ATTRIBUTE',
                  configuration: {
                    allowedTypes: ['integer']
                  }
                }, {
                  value: true,
                  label: 'Autocomplete',
                  key: 'autoComplete',
                  type: 'TOGGLE'
                }, {
                  value: false,
                  label: 'Validation options',
                  key: 'validationOptions',
                  type: 'TOGGLE'
                }, {
                  label: 'Validation pattern',
                  key: 'pattern',
                  value: '',
                  type: 'TEXT',
                  configuration: {
                    placeholder: '[0-9]{8,}',
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
                }, {
                  label: 'Min value',
                  key: 'minvalue',
                  value: '',
                  type: 'NUMBER',
                  configuration: {
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
                }, {
                  label: 'Max value',
                  key: 'maxvalue',
                  value: '',
                  type: 'NUMBER',
                  configuration: {
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
                }, {
                  value: ['This field is required'],
                  label: 'Value required message',
                  key: 'validationValueMissing',
                  type: 'VARIABLE',
                  configuration: {
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
                }, {
                  value: ['Invalid value'],
                  label: 'Pattern mismatch message',
                  key: 'validationPatternMismatch',
                  type: 'VARIABLE',
                  configuration: {
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
                }, {
                  value: ['This value is below the set minimum'],
                  label: 'Value below minimum message',
                  key: 'validationBelowMinimum',
                  type: 'VARIABLE',
                  configuration: {
                    condition: {
                      type: 'SHOW',
                      option: 'validationOptions',
                      comparator: 'EQ',
                      value: true
                    }
                  }
