({
  prefab,
  save,
  close,
  components: {
    ModelSelector,
    Header,
    Content,
    Field,
    Footer,
    Text
  },
  helpers: {
    useModelQuery
  }
}) => {
  const [modelId, setModelId] = React.useState('');
  const [showValidation, setShowValidation] = React.useState(false);
  const {
    data
  } = useModelQuery({
    variables: {
      id: modelId
    },
    skip: !modelId
  });
  React.useEffect(() => {
    setShowValidation(false);
  }, [modelId]);
  return <>
        <Header onClose={close} title="Configure delete record" />
        <Content>
          <Field label="Select model" error={showValidation && <Text color="#e82600">Model is required</Text>} info="Small note: If you can't select any models try to place the button inside a component where an object is available.">
            <ModelSelector onChange={id => {
          setModelId(id);
        }} value={modelId} margin scopedModels />
          </Field>
        </Content>
        <Footer onClose={close} onSave={() => {
      const camelToSnakeCase = str => str[0].toLowerCase() + str.slice(1, str.length).replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);

      if (!modelId || !data || !data.model) {
        setShowValidation(true);
        return;
      }

      const newPrefab = { ...prefab
      };

      if (data && data.model) {
        newPrefab.variables[0].name = camelToSnakeCase(data.model.name);
      }

      newPrefab.structure[0].descendants[1].descendants[0].descendants[0].descendants[0].descendants[2].descendants[1].options[6].value = [modelId];
      newPrefab.variables[0].options.modelId = modelId;
      save(newPrefab);
    }} />
      </>;
}

