({
  prefab,
  save,
  close,
  components: {
    Header,
    Content,
    Field,
    Footer,
    Text,
    EndpointSelector
  }
}) => {
  const [value, setValue] = React.useState({});
  const [showValidation, setShowValidation] = React.useState(false);

  function serializeParameters(obj) {
    return Object.entries(obj).map(([name, entry]) => ({
      name,
      value: entry.map(v => JSON.stringify(v))
    }));
  }

  return <>
        <Header onClose={close} title="Configure logout button" />
        <Content>
          <Field label="Redirect after logout" error={showValidation && <Text color="#e82600">Selecting a page is required</Text>}>
            <EndpointSelector size="large" value={value} onChange={setValue} />
          </Field>
        </Content>
        <Footer onClose={close} onSave={() => {
      if (!Object.keys(value).length) {
        setShowValidation(true);
        return;
      }

      const newPrefab = { ...prefab
      };
      newPrefab.interactions[0].parameters = [{
        parameter: 'redirectTo',
        pageId: value.pageId,
        endpointId: value.id,
        parameters: serializeParameters(value.params)
      }];
      save(newPrefab);
    }} />
      </>;
}

({
  prefab,
  save,
  close,
  components: {
    EndpointSelector,
    Header,
    Content,
    Field,
    Footer,
    Text
  }
}) => {
  const [endpoint, setEndpoint] = React.useState({
    id: '',
    pageId: '',
    params: {}
  });
  const [showValidation, setShowValidation] = React.useState(false);
  const isInternalEndpoint = typeof endpoint === 'object';
  const isExternalEndpoint = typeof endpoint === 'string';
  const isValidEndpoint = isInternalEndpoint && endpoint.id !== '' || isExternalEndpoint && endpoint !== '';
  React.useEffect(() => {
    if (showValidation && isValidEndpoint) setShowValidation(false);
  }, [isValidEndpoint, showValidation, setShowValidation]);

  const onSaveHandler = () => {
    if (!isValidEndpoint) {
      setShowValidation(true);
      return;
    }

    const newPrefab = { ...prefab
    };

    if (isInternalEndpoint) {
      newPrefab.structure[0].options[3].value = endpoint;
    } else {
      newPrefab.structure[0].options[2].value = 'external';
      newPrefab.structure[0].options[4].value = [endpoint];
    }

    save(newPrefab);
  };

  return <>
        <Header onClose={close} title="Configure open page button" />
        <Content>
          <Field label="Link to" error={showValidation && <Text color="#e82600">Page is required</Text>}>
            <EndpointSelector value={endpoint} allowExternal onChange={value => setEndpoint(value)} />
          </Field>
        </Content>
        <Footer onClose={close} onSave={onSaveHandler} onSkip={() => {
      const newPrefab = { ...prefab
      };
      save(newPrefab);
    }} />
      </>;
}

